mapper.select:
SELECT * FROM users WHERE deleted = 0
AND (username LIKE CONCAT('%', #{keyword}, '%') OR real_name LIKE CONCAT('%', #{keyword}, '%') OR email LIKE CONCAT('%', #{keyword}, '%') OR phone LIKE CONCAT('%', #{keyword}, '%'))
AND role = #{role}
AND status = #{status}
ORDER BY created_at DESC

SELECT COUNT(*) FROM users 
WHERE deleted = 0

    AND role = #{role}


    AND status = #{status}
